mapper.select:
select * from (select rownum rnum, R.* from (select * from rent where userid = #{userid} AND RETURN_date IS NULL
and (isbn like '%'||#{keyword}||'%')
and (title like '%'||#{keyword}||'%')
order by rent_date desc) R where
rownum <= #{endNo}
) Q where
rnum >= #{startNo}

select * from (select rownum rnum, R.* from (select * from rent where userid = #{userid} AND RETURN_date IS NOT NULL
and (isbn like '%'||#{keyword}||'%')
and (title like '%'||#{keyword}||'%')
order by rent_date desc) R where
rownum <= #{endNo}
) Q where
rnum >= #{startNo}

select * from (select rownum rnum, R.* from
(SELECT * FROM rent WHERE RETURN_DUE_DATE < SYSDATE AND RETURN_DATE IS NULL
and (isbn like '%'||#{keyword}||'%')
and (title like '%'||#{keyword}||'%')
and (userid like '%'||#{keyword}||'%')
order by rent_id desc) R where
rownum <= #{endNo}
) Q where
rnum >= #{startNo}
